import { useQuery, useMutation, ApolloClient, InMemoryCache, gql } from "@apollo/client";

const client = new ApolloClient({
  uri: "http://localhost:3000/graphql",
  cache: new InMemoryCache(),
});

export const GET_TASKS = gql`
  query {
    tasks {
      id
      name
      iscompleted
    }
  }
`;

export const ADD_TASK = gql`
  mutation AddTask($name: String!) {
    addTask(name: $name) {
      id
      name
      iscompleted
    }
  }
`;

export const DELETE_TASK = gql`
  mutation DeleteTask($id: Int!) {
    deleteTask(id: $id)
  }
`;

export const UPDATE_TASK = gql`
  mutation UpdateTask($id: Int!, $iscompleted: Boolean!) {
    toggleTask(id: $id, iscompleted: $iscompleted) {
      id
      name
      iscompleted
    }
  }
`;

export const useGetTasks = () => {
  return useQuery(GET_TASKS, { client });
};

export const useAddTask = () => {
  return useMutation(ADD_TASK, { client });
};

export const useDeleteTask = () => {
  return useMutation(DELETE_TASK, { client });
};

export const useUpdateTask = () => {
  return useMutation(UPDATE_TASK, { client });
};
