import { createSlice } from "@reduxjs/toolkit";
const initState = {
  todos_ar: [
    { name: "go to work", id: 1 },
    { name: "go to Gym", id: 2 },
    { name: "go to home", id: 3 },
    { name: "go to shnatz", id: 4 },
    { name: "go to eat", id: 5 },
    { name: "go to sleep", id: 6 },
  ],
};

const todoSlice = createSlice({
  name: "todo",
  initialState: initState,
  reducers: {
    addNewItem: (state, action) => {
      state.todos_ar.push(action.payload.todoItem);
    },
    delItem: (state, action) => {
        state.todos_ar= state.todos_ar.filter((item)=> item.id!==action.payload.delId)
    },
  },
});

export const {addNewItem, delItem} = todoSlice.actions ;
export default todoSlice.reducer;
