import { createSlice } from '@reduxjs/toolkit';
import { gql, useQuery, useMutation } from '@apollo/client';
import { useDispatch } from 'react-redux';

const todoSlice = createSlice({
  name: 'todo',
  initialState: { todos_ar: [] },
  reducers: {
    setTasks: (state, action) => {
      state.todos_ar = action.payload.tasks;
    },
//     addNewItem: (state, action) => {
//       state.todos_ar.push(action.payload.todoItem);
//     },
    delItem: (state, action) => {
      state.todos_ar = state.todos_ar.filter((item) => item.id !== action.payload.delId);
    },
  },
});

// GraphQL query definition to fetch tasks
const GET_TASKS = gql`
  query {
    tasks {
      id
      name
    }
  }
`;

// GraphQL mutation definition to add a new task
const ADD_TASK = gql`
  mutation AddTask($name: String) {
    addTask(name: $name) {
      id
      name
    }
  }
`;

// GraphQL mutation definition to delete a task
const DELETE_TASK = gql`
  mutation DeleteTask($id: Int) {
    deleteTask(id: $id)
  }
`;

// Action creator to fetch tasks from the server
export const fetchTasks = () => {
  return async (dispatch) => {
    try {
      const { data } = await useQuery(GET_TASKS);
      dispatch(todoSlice.actions.setTasks(data.tasks));
    } catch (error) {
      console.log('Error fetching tasks:', error);
      throw new Error('Failed to fetch tasks');
    }
  };
};

// Action creator to add a new task using the Apollo mutation
export const addNewTask = (name) => {
  return async (dispatch) => {
    try {
      const [addTask] = useMutation(ADD_TASK);
      const response = await addTask({ variables: { name } });
      dispatch(todoSlice.actions.addNewItem(response.data.addTask));
    } catch (error) {
      console.log('Error adding task:', error);
      throw new Error('Failed to add task');
    }
  };
};

// Action creator to delete a task using the Apollo mutation
export const deleteTask = (id) => {
  return async (dispatch) => {
    try {
      const [deleteTask] = useMutation(DELETE_TASK);
      const response = await deleteTask({ variables: { id } });
      if (response.data.deleteTask) {
        dispatch(todoSlice.actions.delItem({ delId: id }));
      }
    } catch (error) {
      console.log('Error deleting task:', error);
      throw new Error('Failed to delete task');
    }
  };
};

export default todoSlice.reducer;
