import { Checkbox, IconButton, ListItem, ListItemButton, ListItemIcon, ListItemText} from '@mui/material'
import DeleteIcon from '@mui/icons-material/Delete';
// import CommentIcon from '@mui/icons-material/Comment';
import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import { deleteTask } from '../features/todoSlice';
import { TaskProps } from './todoList';

function TodoItem(props:TaskProps) {
    const dispatch = useDispatch();

    const [checked, setChecked] = useState(false);

    const handleToggle = () => {
       setChecked(!checked)
    };


  return (
    <div>
         <ListItem
            // key={props.task}
            secondaryAction={
              <IconButton onClick = {() => {dispatch(deleteTask({delId:props.task.id}))}} edge="end" aria-label="comments">
               <DeleteIcon style={{color:'red'}}/>
              </IconButton>
            }
            disablePadding
          >
            <ListItemButton>
              <ListItemIcon onClick={handleToggle}>
                <Checkbox
                  edge="start"

                />
              </ListItemIcon>
              <ListItemText primary={`${props.task.item}`} style={{fontStyle: checked ? 'italic': 'normal', textDecorationLine: checked ? 'line-through': 'none', color: checked ? 'gray' : 'black'}}/>
            </ListItemButton>
          </ListItem>
    </div>
  )
}

export default TodoItem
