import {
  Checkbox,
  IconButton,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import React, { useEffect, useState } from "react";
import { useMutation } from "@apollo/client";
import { TOGGLE_TASK, DELETE_TASK } from "../service/apiService";
import { TaskProps } from "./todoList";
import { actions } from "../features/todoSlice";
import { useDispatch } from "react-redux";

export function TodoItem(props: TaskProps) {
  const dispatch = useDispatch();
  const [checked, setChecked] = useState(props.task.iscompleted);
  const [deleteTaskMutation] = useMutation(DELETE_TASK);
  const [toggleTaskMutation, { data: toggleTask }] = useMutation(TOGGLE_TASK);
  // useEffect(() => {
  //   if (toggleTask) {
  //     setChecked(!checked);
  //     console.log("pppppppppp");
  //   }
  // }, [toggleTask]);

  const handleToggle = async () => {
    try {
      await toggleTaskMutation({ variables: { id: props.task.id } });
      dispatch(actions.toggleTask(toggleTask));

      setChecked(!checked);
    } catch (error) {
      console.error("Error toggling task:");
    }
  };

  const handleDelete = async () => {
    deleteTaskMutation({ variables: { id: props.task.id } });
    dispatch(actions.deleteTask({ delId: props.task.id }));
  };

  return (
    <ListItem disablePadding>
      <ListItemButton>
        <ListItemIcon>
          <Checkbox edge="start" checked={checked} onChange={handleToggle} />
        </ListItemIcon>
        <ListItemText
          primary={props.task.name}
          style={{
            fontStyle: checked ? "italic" : "normal",
            textDecorationLine: checked ? "line-through" : "none",
            color: checked ? "gray" : "black",
          }}
        />
        <IconButton onClick={handleDelete} edge="end" aria-label="delete">
          <DeleteIcon style={{ color: "red" }} />
        </IconButton>
      </ListItemButton>
    </ListItem>
  );
}
