import { Button, Grid, Input } from "@mui/material";
import React, { useRef } from "react";
import { useDispatch } from "react-redux";
import { addTask } from "../features/todoSlice";
import { createTask } from "../service/apiService";

function AddTodo() {
  const dispatch = useDispatch();

  const newVal = useRef<HTMLInputElement | null>(null);

  const add = async (event: React.FormEvent) => {
    event.preventDefault();
    if (newVal.current) {
      const taskName = newVal.current.value;
      await createTask(taskName); // Create the task in the database
      dispatch(addTask({ name: taskName })); // Update the task list
      newVal.current.value = "";
    }
  };

  return (
    <form onSubmit={add}>
      <Grid container spacing={12}>
        <Grid item xs={6}>
          <Input inputRef={newVal} placeholder="Placeholder" required />
        </Grid>
        <Grid item xs>
          <Button type="submit" variant="contained">
            ADD TODO
          </Button>
        </Grid>
      </Grid>
    </form>
  );
}

export default AddTodo;
