import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { getTasks, addTaskToDb } from "../service/apiService";

const initialState = {
  todoList: []
};

export const addTask = createAsyncThunk(
  "todoList/addTask",
  async (taskName, { dispatch }) => {
    await addTaskToDb(taskName);
    const tasks = await getTasks();
    dispatch(setTodoList(tasks));
  }
);

const todoSlice = createSlice({
  name: "todoList",
  initialState,
  reducers: {
    setTodoList: (state, action) => {
      state.todoList = action.payload;
    },
    deleteTask: (state, action) => {
      // Implementation for deleting a task from the state
    },
  },
});

export const { setTodoList, deleteTask } = todoSlice.actions;
export const { actions } = todoSlice;
export default todoSlice.reducer;
