import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
    import { getTasks } from "../services/apiServices";
    
    export const fetchTasks = createAsyncThunk('todoList/fetchTasks', async () => {
      const tasks = await getTasks();

      return tasks;
      
    });
    
    const todoSlice = createSlice({
      name: "todoList",
      initialState: { listTask: [] },
      reducers: {
        addTask: (state, action) => {
          state.listTask.push(action.payload.newTask);
        },
        deleteTask: (state, action) => {
          state.listTask = state.listTask.filter(
            (item) => item.id !== action.payload.delId
          );
        },
      },
      
      extraReducers: (builder) => {
        builder
          .addCase(fetchTasks.fulfilled, (state, action) => {
            state.listTask = action.payload;
          })
          .addCase(fetchTasks.rejected, (state, action) => {
            state.listTask = [];
          });
      },
    });
    
    export const { addTask, deleteTask } = todoSlice.actions;
    export default todoSlice.reducer;
