import { Checkbox, IconButton, ListItem, ListItemButton, ListItemIcon, ListItemText } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { deleteTask } from '../features/todoSlice';
import { useMutation } from '@apollo/client';
import { DELETE_TASK, GET_TASKS } from '../service/apiService';

function TodoItem(props) {
  const dispatch = useDispatch();

  const [checked, setChecked] = useState(false);

  const handleToggle = () => {
    setChecked(!checked);
  };

  const [deleteTaskMutation] = useMutation(DELETE_TASK, {
    refetchQueries: [{ query: GET_TASKS }], // Refetch the tasks query after deletion
  });

  const handleDelete = async () => {
    try {
      await deleteTaskMutation({ variables: { id: props.task.id } });
      dispatch(deleteTask({ delId: props.task.id }));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <ListItem disablePadding>
      <ListItemButton>
        <ListItemIcon onClick={handleToggle}>
          <Checkbox edge="start" checked={checked} />
        </ListItemIcon>
        <ListItemText
          primary={props.task.name}
          style={{
            fontStyle: checked ? 'italic' : 'normal',
            textDecorationLine: checked ? 'line-through' : 'none',
            color: checked ? 'gray' : 'black',
          }}
        />
        <IconButton onClick={handleDelete} edge="end" aria-label="delete">
          <DeleteIcon style={{ color: 'red' }} />
        </IconButton>
      </ListItemButton>
    </ListItem>
  );
}

export default TodoItem;
