import React from "react";
import { useQuery, useMutation, HttpLink, ApolloClient, InMemoryCache } from "@apollo/client";
import { gql } from "@apollo/client";
// import { useDispatch, useSelector } from "react-redux";

const httpLink = new HttpLink({
  uri: "http://localhost:3000/graphql/"
})

export const apolloClient = new ApolloClient({
  link: httpLink,
  cache: new InMemoryCache()
});

// // Define your GraphQL queries and mutations
export const GET_TASKS = gql`
  query {
    tasks {
      id
      name
      iscompleted
    }
  }
`;


export const ADD_TASK = gql`
  mutation AddTask($name: String!) {
    addTask(name: $name) {
      id
      name
      iscompleted
    }
  }
`;

export const DELETE_TASK = gql`
  mutation DeleteTask($id: Int!) {
    deleteTask(id: $id)
  }
`;

export const  TOGGLE_TASK = gql`
  mutation ToggleTask($id: Int!) {
    deleteTask(id: $id)
  }
`;


export const getTasks = async() => {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const { loading, error, data } = useQuery(GET_TASKS);
  
    console.log(data);
    
    if (loading) {
      // Return a loading state or null while data is being fetched
      return null;
    }
  
    if (error) {
      // Handle the error condition
      console.error("Error fetching tasks:", error);
      return [];
    }
  
    return data.tasks;
  };
