import React, { useEffect, useRef } from "react";
import { useDispatch } from "react-redux";
import { useMutation, useQuery } from "@apollo/client";
import { actions, addTask } from "../features/todoSlice";
import { ADD_TASK, GET_TASKS } from "../service/apiService";
import { Button, Grid, Input } from "@mui/material";

function AddTodo() {
  const dispatch = useDispatch();

  const newVal = useRef<HTMLInputElement | null>(null);

  const [addTaskMutation , {data}] = useMutation(ADD_TASK);
  // const { refetch: refetchTasks } = useQuery(GET_TASKS);

  const add = async (event: React.FormEvent) => {
    event.preventDefault();
    
    useEffect(() => {
      if (data) {
  
         console.log(data);
        dispatch(actions.setTodoList(data.tasks));
      }
      
    }, [data]);
    if (newVal.current && newVal.current.value) {
      try {
        const taskName = newVal.current.value;
        await addTaskMutation({ variables: { name: taskName } });
        // dispatch(actions.addTask({ addTask:  addTaskMutation  }));
        // await refetchTasks();
        newVal.current.value = "";
      } catch (error) {
        console.error("Error adding task:", error);
      }
    }
  };

  return (
    <form onSubmit={add}>
      <Grid container spacing={12}>
        <Grid item xs={6}>
          <Input inputRef={newVal} placeholder="Placeholder" required />
        </Grid>
        <Grid item xs>
          <Button type="submit" variant="contained">
            ADD TODO
          </Button>
        </Grid>
      </Grid>
    </form>
  );
}

export default AddTodo;
