import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import TodoItem from "./todoItem";
import { Grid, Paper, styled } from "@mui/material";
import AddTodo from "./addToDo";
import { GET_TASKS, apolloClient } from "../service/apiService";
import { actions } from "../features/todoSlice";
import { useQuery } from "@apollo/react-hooks";

export interface TaskProps {
  task: { name: string; id: number };
}

function TodoList() {
  const dispatch = useDispatch();
  const todoList = useSelector((state: any) => state.todoSlice.todoList);

  const { loading, error, data } = useQuery(GET_TASKS);

  useEffect(() => {
    if (data) {
      dispatch(actions.setTodoList(data.tasks));
    }
  }, [data]);

  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: "center",
    color: theme.palette.text.secondary,
  }));

  return (
    <Grid container spacing={6}>
      {todoList && (
        <Grid item xs={5} style={{ margin: "0 auto" }}>
          <h1>Todo List</h1>
          // eslint-disable-next-line react/jsx-no-undef
          <Item>
            {todoList.map((task: { name: string; id: number }) => {
              return <TodoItem task={task} key={task.id} />;
            })}
            <AddTodo />
          </Item>
        </Grid>
      )}
    </Grid>
  );
}

export default TodoList;
