import { Checkbox, IconButton, ListItem, ListItemButton, ListItemIcon, ListItemText } from '@mui/material'
import React, { useState } from 'react';
import DeleteIcon from '@mui/icons-material/Delete';
import { useDispatch } from 'react-redux';
import { delItem } from '../features/todoSlice';
import { Task } from './todoList';

function TodoItem(props:Task){
    const dispatch = useDispatch();
    const [checked, setChacked] = useState(false);
    const handletoggel = ()=>{
        setChacked(!checked);
    }

  return (
    <ListItem
    key={props.id}
    secondaryAction={
      <IconButton edge="end" aria-label="comments" onClick={()=>{dispatch(delItem({delId: props.id}))}}>

          <DeleteIcon style={{color: 'red'}}/>
      </IconButton >
    }
    disablePadding
  >
    <ListItemButton onClick={handletoggel}>
      <ListItemIcon>
        <Checkbox
          edge="start"
        />
      </ListItemIcon>
      <ListItemText primary={`${props.name}`} style={{fontStyle: checked ? 'italic': 'normal', textDecorationLine: checked ? 'line-through': 'none' , color: checked? 'gray' : 'black'}}/>
    </ListItemButton>
  </ListItem>
  )
}

export default TodoItem
