import { Checkbox, IconButton, ListItem, ListItemButton, ListItemIcon, ListItemText } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useMutation } from '@apollo/client';
import { TOGGLE_TASK, DELETE_TASK } from '../service/apiService';
import { TaskProps } from './todoList';

function TodoItem(props: TaskProps) {
  const [checked, setChecked] = useState(props.task.iscompleted);

  const [toggleTaskMutation] = useMutation(TOGGLE_TASK);

  const handleToggle = async () => {
    try {
      await toggleTaskMutation({ variables: { id: props.task.id } });
      setChecked(!checked);
    } catch (error) {
      console.error('Error toggling task:', error);
    }
  };

  const handleDelete = async () => {
    try {
      // Call the DELETE_TASK mutation with the task ID
      await deleteTaskMutation({ variables: { id: props.task.id } });

      // Handle any additional state updates or actions after successful deletion if needed
      // For example, you can remove the task from the local state or trigger a Redux action.
      // Example: dispatch(deleteTaskAction(props.task.id));

    } catch (error) {
      console.error('Error deleting task:', error);
    }  };

  return (
    <ListItem disablePadding>
      <ListItemButton>
        <ListItemIcon>
          <Checkbox edge="start" checked={checked} onChange={handleToggle} />
        </ListItemIcon>
        <ListItemText
          primary={props.task.name}
          style={{
            fontStyle: checked ? 'italic' : 'normal',
            textDecorationLine: checked ? 'line-through' : 'none',
            color: checked ? 'gray' : 'black',
          }}
        />
        <IconButton onClick={handleDelete} edge="end" aria-label="delete">
          <DeleteIcon style={{ color: 'red' }} />
        </IconButton>
      </ListItemButton>
    </ListItem>
  );
}

export default TodoItem;
