import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { TaskProps } from "../comps/todoList";

export interface Task {
  name: string;
  id: number;
  iscompleted: boolean;
}
export interface TodoStateInterface {
  todoSlice: {
    todoList: Task[];
  };
}

const initialState = {
  todoList: [] as Task[],
};


export const fetchTasks = createAsyncThunk("todoList/fetchTasks", async () => {
  const response = await GET_TASKS(); // Replace with the actual API function to fetch tasks
  return response.tasks;
});

const todoSlice = createSlice({
  name: "todoList",
  initialState,
  reducers: {
    setTodoList: (state, action) => {
      state.todoList = action.payload;
    },
    addTask: (state, action) => {
      state.todoList.push(action.payload);
      console.log(action.payload);
    },

    deleteTask: (state, action) => {
      state.todoList = state.todoList.filter(
        (item) => item.id !== action.payload.delId
      );
    },
  },
});

// export { fetchTasks };
export const { addTask, deleteTask } = todoSlice.actions;
// export default todoSlice.reducer;

export const { actions } = todoSlice;
export default todoSlice.reducer;
