import { Button, Grid, Input } from "@mui/material";
import React, { useRef } from "react";
import { useDispatch } from "react-redux";
import { addTask } from "../features/todoSlice";
import { useMutation } from "@apollo/client";
import { ADD_TASK } from "../service/apiService";

function AddTodo() {
  const dispatch = useDispatch();

  const newVal = useRef<HTMLInputElement | null>(null);

  const add = (event: React.FormEvent) => {
    event.preventDefault();
    console.log(newVal);
    if (newVal.current) {
      dispatch(
        addTask({ newTask: { name: newVal.current.value} }) 
      );
      
      newVal.current.value = "";
    }

    
  };

  return (
    <form onSubmit={add}>
      <Grid container spacing={12}>
        <Grid item xs={6}>
          <Input inputRef={newVal} placeholder="Placeholder" required/>
        </Grid>
        <Grid item xs>
          <Button type="submit" variant="contained">
            ADD TODO
          </Button>
        </Grid>
      </Grid>
    </form>
  );
}

export default AddTodo;
